    }
  return (
    <section className='challenge'>
        <h2>{title}</h2>
        {timerExpired && <p>You lost!</p>}
        <p className='challenge-time'>
            {targetTime} second {targetTime > 1 ? 's':''}
        </p>
       <p>
       <button onClick={handleStart}>
            {timerStarted? 'Stop':'Start'}Challenge
